typedef struct {
    uint32_t baud_rate;     // 波特率 [9600-115200]
    uint8_t data_bits;      // 数据位 [5-8]
    uint8_t stop_bits;      // 停止位 [1-2]
    uint8_t parity;         // 校验位 [0:无, 1:奇, 2:偶]
} uart_config_t;

uart_config_t g_st_uart_config;

typedef struct {
    float kp;               // 比例系数
